```javascript
function createChart(data) {
  const chartData = {
    labels: data.map((entry) => entry[1]), // Use the timestamps as labels
    datasets: data.reduce((datasets, entry) => {
      const userId = entry[0];
      const value = entry[2];

      if (!datasets[userId]) {
        datasets[userId] = {
          label: `User ${userId}`,
          data: [],
          borderColor: `rgb(${Math.random() * 255}, ${Math.random() * 255}, ${Math.random() * 255})`,
          fill: false,
        };
      }

      datasets[userId].data.push(value);

      return datasets;
    }, {}),
  };

  const chartOptions = {
    responsive: true,
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'hour',
          displayFormats: {
            hour: 'HH:mm',
          },
        },
        ticks: {
          source: 'labels',
        },
      },
      y: {
        beginAtZero: true,
      },
    },
  };

  const ctx = document.getElementById('chart').getContext('2d');
